import React from 'react';
import { Button, Card } from 'react-bootstrap';
import { FaCreditCard, FaShippingFast } from 'react-icons/fa';

const styles = {
  body: {
    fontFamily: "'Arial', sans-serif",
    lineHeight: 1.6,
    backgroundColor: '#f4f4f9',
    color: '#333',
    padding: '0 1rem',
  },
  section: {
    padding: '4rem 2rem',
    margin: '0 auto',
    maxWidth: '1200px',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center',
  },
  sectionTitle: {
    marginBottom: '1.5rem',
    color: '#333',
    fontSize: '2rem',
    fontWeight: 'bold',
  },
  pricingCard: {
    maxWidth: '600px',
    margin: '0 auto',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    border: 'none',
  },
  cardBody: {
    padding: '2rem',
    textAlign: 'center',
  },
  pricingText: {
    fontSize: '1.2rem',
    margin: '1rem 0',
    color: '#333',
  },
  buyButton: {
    marginTop: '1.5rem',
    padding: '1rem 2rem',
    fontSize: '1.25rem',
    backgroundColor: '#ff4081',
    borderColor: '#ff4081',
    color: 'white',
  },
  contactSection: {
    padding: '4rem 2rem',
    backgroundColor: '#f7f7f7',
    textAlign: 'center',
  },
  contactButton: {
    marginTop: '1rem',
    padding: '0.75rem 2rem',
    backgroundColor: '#6a82fb',
    borderColor: '#6a82fb',
    color: 'white',
  },
  icon: {
    fontSize: '1.5rem',
    marginRight: '0.5rem',
  },
};

const BuyNowPage = () => {
  return (
    <div style={styles.body}>
      {/* Hero Section */}
      <section style={styles.section}>
        <h2 style={styles.sectionTitle}>Buy SensAIra Now</h2>
        <p>Get your AI-powered health monitoring wristband today and start monitoring your health in real-time.</p>
      </section>

      {/* Pricing Section */}
      <section style={styles.section}>
        <Card style={styles.pricingCard}>
          <Card.Body style={styles.cardBody}>
            <h3 style={{ fontSize: '2rem', fontWeight: 'bold' }}>Pricing</h3>
            <p style={styles.pricingText}><strong>₹1999</strong> for the wristband (includes 3 months of free subscription)</p>
            <p style={styles.pricingText}><strong>₹99/month</strong> after the first 3 months (₹149/month after 6 months)</p>
            <p style={styles.pricingText}>Free shipping and easy returns!</p>
            <Button variant="success" size="lg" style={styles.buyButton}>
              <FaCreditCard style={styles.icon} />
              Buy Now
            </Button>
            <p className="mt-3" style={{ fontSize: '1rem', color: '#666' }}><FaShippingFast style={styles.icon} /> Free shipping available!</p>
          </Card.Body>
        </Card>
      </section>

      {/* Contact Assistance Section */}
      <section style={styles.contactSection}>
        <h2 style={styles.sectionTitle}>Need Assistance?</h2>
        <p>If you have any questions or need help with your order, feel free to contact us.</p>
        <Button variant="primary" href="/contact" style={styles.contactButton}>
          Contact Us
        </Button>
      </section>
    </div>
  );
};

export default BuyNowPage;
