import React from 'react';
import { Button, Card } from 'react-bootstrap';
import { FaHeartbeat, FaMobileAlt, FaMoneyBillWave, FaPhone } from 'react-icons/fa';
import { motion } from 'framer-motion';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import HomePage from './pages/HomePage';
import ProductPage from './pages/ProductPage';

// Features Data
const features = [
  { icon: FaHeartbeat, title: "Predictive Analytics", desc: "AI-powered health monitoring to detect risks early." },
  { icon: FaMobileAlt, title: "Real-Time Monitoring", desc: "Track vitals like heart rate, SpO2, and stress levels." },
  { icon: FaMoneyBillWave, title: "Affordable Pricing", desc: "₹1999 upfront + ₹99/month for full health insights." }
];

// Main App Component


export default App;

// SensAIraLanding Component
export function SensAIraLanding() {
  return (
    <div className="min-vh-100">
      {/* Hero Section */}
      <section className="text-center py-5 bg-primary text-white">
        <motion.h1 className="display-4" initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          Welcome to sensAIra
        </motion.h1>
        <p className="mt-4 lead">AI-powered wearable for proactive health monitoring</p>
        <Button variant="light" className="mt-4">Get Started</Button>
      </section>

      {/* Features Section */}
      <section className="py-5 px-3 text-center">
        <div className="row">
          {features.map((feature, idx) => (
            <div className="col-md-4 mb-4" key={idx}>
              <Card className="shadow-lg">
                <Card.Body>
                  <feature.icon className="text-primary mb-3" style={{ fontSize: '2.5rem' }} />
                  <Card.Title>{feature.title}</Card.Title>
                  <Card.Text>{feature.desc}</Card.Text>
                </Card.Body>
              </Card>
            </div>
          ))}
        </div>
      </section>

      {/* Pricing Section */}
      <section className="text-center py-5 bg-light">
        <h2 className="h2 font-weight-bold">Simple, Transparent Pricing</h2>
        <p className="lead">₹1999 upfront + ₹99/month after 3 months</p>
        <Button variant="primary" className="mt-4">Buy Now</Button>
      </section>

      {/* Contact Section */}
      <section className="text-center py-5">
        <h2 className="h2 font-weight-bold">Get in Touch</h2>
        <p className="mt-4">Have questions? Reach out to us!</p>
        <div className="mt-4">
          <FaPhone className="text-primary" style={{ fontSize: '2rem' }} />
          <span className="ml-2">+91 98765 43210</span>
        </div>
      </section>
    </div>
  );
}
