import React from 'react';
import { Button, Card, ListGroup } from 'react-bootstrap';
import { FaHeartbeat, FaMobileAlt, FaMoneyBillWave } from 'react-icons/fa';

// Inline CSS
const styles = {
  body: {
    fontFamily: "'Arial', sans-serif",
    lineHeight: 1.6,
    backgroundColor: '#f4f4f9',
    color: '#333',
    padding: '0 1rem',
  },
  section: {
    padding: '4rem 2rem',
    margin: '0 auto',
    maxWidth: '1200px',
    borderRadius: '8px',
    backgroundColor: '#fff',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    marginBottom: '2rem',
  },
  sectionTitle: {
    marginBottom: '1.5rem',
    color: '#333',
    textAlign: 'center',
  },
  button: {
    marginTop: '2rem',
    padding: '0.75rem 1.5rem',
    fontSize: '1.25rem',
    backgroundColor: '#ff4081',
    borderColor: '#ff4081',
  },
  listGroupItem: {
    display: 'flex',
    alignItems: 'center',
    fontSize: '1rem',
    padding: '1rem',
    border: 'none',
    backgroundColor: '#f4f4f9',
  },
  icon: {
    marginRight: '1rem',
    fontSize: '1.5rem',
    color: '#6a82fb',
  },
  img: {
    maxWidth: '100%',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  },
};

const ProductPage = () => {
  return (
    <div style={styles.body}>
      {/* Product Details Section */}
      <section className="text-center py-5" style={styles.section}>
        <h2 className="font-weight-bold" style={styles.sectionTitle}>Product Details</h2>
        <p><strong>Introducing sensAIra:</strong> An AI-powered platform that helps predict and monitor health conditions like hypertension and COPD using data from our wearable wristband.</p>
        <Button variant="primary" style={styles.button}>Buy Now</Button>
      </section>

      {/* Features and How It Works Section */}
      <section className="py-5" style={styles.section}>
        <div className="row">
          <div className="col-md-6">
            <h3 style={styles.sectionTitle}>Key Features</h3>
            <ListGroup>
              <ListGroup.Item style={styles.listGroupItem}>
                <FaHeartbeat style={styles.icon} />
                Predictive Analytics: AI-powered health monitoring to detect risks early.
              </ListGroup.Item>
              <ListGroup.Item style={styles.listGroupItem}>
                <FaMobileAlt style={styles.icon} />
                Real-Time Monitoring: Continuous tracking of vitals like heart rate, SpO2, and respiratory rate.
              </ListGroup.Item>
              <ListGroup.Item style={styles.listGroupItem}>
                <FaMoneyBillWave style={styles.icon} />
                Affordable Pricing: ₹1999 upfront + ₹99/month after the first 3 months.
              </ListGroup.Item>
            </ListGroup>
          </div>
          <div className="col-md-6">
            <h3 style={styles.sectionTitle}>How It Works</h3>
            <p>Our wristband continuously monitors your vitals and uses AI to predict potential health risks. The platform generates real-time insights, emergency alerts, and personalized care recommendations.</p>
            <img src="/images/product.jpg" alt="SensAIra Device" className="img-fluid" style={styles.img} />
          </div>
        </div>
      </section>

      {/* Pricing Section */}
      <section className="text-center py-5 bg-light" style={styles.section}>
        <h2 style={styles.sectionTitle}>Pricing</h2>
        <p>₹1999 for the device + 3 months free subscription. ₹99/month thereafter.</p>
        <Button variant="primary" className="mt-4" style={styles.button}>Buy Now</Button>
      </section>
    </div>
  );
};

export default ProductPage;
